const poemForm=document.querySelector("#poemForm"),typeSelect=document.querySelector("#poemForm select"),themeInput=document.querySelector("#poemForm input"),output=document.querySelector(".output");async function fetchPoem(){const e=typeSelect.value,t=themeInput.value,o=`https://dichter.responsible-it.nl/api/poetry?type=${e}&theme=${encodeURIComponent(t)}`,n={Authorization:"Bearer dichter-a6a8ae46-4c90"};try{const e=await fetch(o,{headers:n});if(!e.ok)throw new Error(`HTTP error ${e.status}`);const t=await e.json();output.textContent=t.data.paragraph}catch(e){console.error("Er is een fout opgetreden:",e)}}poemForm.addEventListener("submit",e=>{e.preventDefault(),fetchPoem()}),document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector("header ul");document.getElementById("theme").addEventListener("input",t=>{const o=t.target.value.split(",").map(e=>e.trim());e.innerHTML="",o.forEach(t=>{const o=document.createElement("li");o.innerText=t,e.appendChild(o)})})});